async () => {
  const isTestData = false;
  if (isTestData) {
    return products.testData;
  } else {
    const productsData = await getProductsApi.data;
    const mappingConfig = {
      id: 'id',
      name: 'name',
      image: 'product_image',
      price: 'sale_price',
      description: 'description'
    };
    return products.productDataMapping(productsData, mappingConfig);
  }
}
