actionConfiguration.body:
(data, mappingConfig) => {
  return data.map(item => {
    const mappedItem = {};
    for (const [key, value] of Object.entries(mappingConfig)) {
      if (item[value]) {
        mappedItem[key] = item[value];
      }
    }
    return mappedItem;
  });
}

